class Box {
	int width; // 멤버 변수 - 객체 변수
	int height; // 클래스 전역에서 사용 가능
	int depth;
	
	
	
	public Box5() {
		width = 1;	// 매개 변수(갈색) 값을 객체 변수(파란색)에 저장
		height = 1;
		depth = 1;
	}
	
	
	public Box(int w) {
		width = w;
		height = 2;
		depth = 3;
	}
}
